statements.insert:
select TT_BENHNHAN_S.nextval as value from dual
declare vrkeyValue int := 0; vrBenhVienId varchar(10):= '$BENHVIEN_ID$'; vryearNow int := TO_NUMBER(EXTRACT(YEAR FROM SYSDATE)); vrMayte varchar(20); vrSoVaoVien varchar(20); vrBenhnhanId int := 0; vrManoisinh varchar(10) := NVL('$MANOISINH$','0'); vrThangsinh int := 0; vrDaysinh int := 0; vrNamsinh int := NVL($NAMSINH$,0); vrNgaysinh DATE := null; vrGioitinh int := $GIOITINH$; vrfid varchar(20); BEGIN begin select KEYVALUE into vrkeyValue from LST_KEYDATA where KEYTYPE = 'MAYTE' and KEYYEAR = vryearNow and BENHVIEN_ID = vrBenhVienId and rownum = 1; EXCEPTION WHEN NO_DATA_FOUND THEN Insert into LST_KEYDATA values ('MAYTE', vryearNow, '0', '0', '0', '0', '0', 0, vrBenhVienId); end; Update LST_KEYDATA set KEYVALUE = KEYVALUE + 1 where KEYTYPE = 'MAYTE' and KEYYEAR = vryearNow and BENHVIEN_ID = vrBenhVienId returning KEYVALUE into vrkeyValue; COMMIT WORK; vrSoVaoVien := LPAD(TO_CHAR(vrkeyValue), 7, '0'); vrMayte := SUBSTR(vryearNow, 3) || vrSoVaoVien;
vrNgaysinh := TO_DATE(REPLACE('$NGAYSINH$', 'AM',''),'mm-dd-yy hh24:mi:ss'); vrBenhnhanId := #ID#;
insert into TT_BENHNHAN ( BENHNHAN_ID , MAYTE , SOVAOVIEN , TENBENHNHAN , HO , TEN , GIOITINH , NGAYSINH , NAMSINH , MANOISINH , SONHA , DIACHI , SODIENTHOAI , XAPHUONG_ID , QUANHUYEN_ID , TINHTHANH_ID , DANTOC_ID , QUOCTICH_ID , SOLUUTRUNOITRU , SOLUUTRUNGOAITRU , NGHENGHIEP_ID , CMND , HOCHIEU , VIETKIEU , NGUOINUOCNGOAI , DIACHITHUONGTRU , DIACHILIENLAC , DIACHICOQUAN , EMAIL , NHOMMAU_ID , YEUTORH_ID , TIENSUDIUNG , TIENSUBENH , TIENSUHUTTHUOCLA , TUVONG , NGAYTUVONG , THOIGIANTUVONG , NGUYENNHANTUVONG_ID , NGUOIGHINHANTUVONG_ID , THOIGIANGHINHANTUVONG , NHANVIEN_ID , DONVICONGTAC_ID , DIENTHOAIBAN , TINHTRANGHONNHAN_ID , NGUOILIENHE_ID , MOIQUANHE_ID , NGUOILIENHE , THONGTIN_NLH , GHICHU , HINHANH_DAIDIEN , SITE_ID , PID , ACTIVE , BENHVIEN_ID , NGAYTAO , NGUOITAO_ID , NGAYGIOSINH ) values ( vrBenhnhanId , vrMayte , vrSoVaoVien , #TENBENHNHAN# , #HO# , #TEN# , #GIOITINH# , #NGAYSINH# , #NAMSINH# , #MANOISINH# , #SONHA# , #DIACHI# , #SODIENTHOAI# , #XAPHUONG_ID# , #QUANHUYEN_ID# , #TINHTHANH_ID# , #DANTOC_ID# , #QUOCTICH_ID# , #SOLUUTRUNOITRU# , #SOLUUTRUNGOAITRU# , #NGHENGHIEP_ID# , #CMND# , #HOCHIEU# , #VIETKIEU# , #NGUOINUOCNGOAI# , #DIACHITHUONGTRU# , #DIACHILIENLAC# , #DIACHICOQUAN# , #EMAIL# , #NHOMMAU_ID# , #YEUTORH_ID# , #TIENSUDIUNG# , #TIENSUBENH# , #TIENSUHUTTHUOCLA# , #TUVONG# , #NGAYTUVONG# , #THOIGIANTUVONG# , #NGUYENNHANTUVONG_ID# , #NGUOIGHINHANTUVONG_ID# , #THOIGIANGHINHANTUVONG# , #NHANVIEN_ID# , #DONVICONGTAC_ID# , #DIENTHOAIBAN# , #TINHTRANGHONNHAN_ID# , #NGUOILIENHE_ID# , #MOIQUANHE_ID# , #NGUOILIENHE# , #THONGTIN_NLH# , #GHICHU# , #HINHANH_DAIDIEN# , #SITE_ID# , #PID# , '1' , #BENHVIEN_ID# , #NGAYTAO# , #NGUOITAO_ID# , #NGAYGIOSINH# ); if (vrNgaysinh != NULL) then vrThangsinh := TO_NUMBER(EXTRACT(MONTH FROM vrNgaysinh)); vrDaysinh := TO_NUMBER(EXTRACT(DAY FROM vrNgaysinh)); end if; vrfid := LPAD(TO_CHAR(vrManoisinh), 3, '0') || TO_CHAR(vrGioitinh) || SUBSTR(vrNamsinh, 3) || LPAD(TO_CHAR(vrThangsinh), 2, '0')|| LPAD(TO_CHAR(vrDaysinh), 2, '0')|| LPAD(TO_CHAR(vrBenhnhanId), 9, '0') || 'F'; update TT_BENHNHAN set FID = vrfid where BENHNHAN_ID = vrBenhnhanId;
END;

statements.statement:
BEGIN
delete from TT_BENHNHAN_BHYT where BENHNHAN_ID = '$BENHNHAN_ID$';
delete from TT_BENHNHAN where BENHNHAN_ID = '$BENHNHAN_ID$';
END;
