statements.insert:
select TT_TIEPNHAN_S.nextval as value from dual
DECLARE vrkeyValue int := 0; vrBenhVienId varchar(10):= '$BENHVIEN_ID$'; vryearNow int:= TO_NUMBER(EXTRACT(YEAR FROM SYSDATE)); vrmonthNow varchar(2) := LPAD(TO_CHAR(EXTRACT(MONTH FROM SYSDATE)), 2, '0'); vrdayNow varchar(2) := LPAD(TO_CHAR(EXTRACT(DAY FROM SYSDATE)), 2, '0'); vrSotiepnhan varchar(20); vrSoThuTu varchar(20); BEGIN begin select KEYVALUE into vrkeyValue from LST_KEYDATA where KEYTYPE = 'SOTIEPNHAN' and KEYYEAR = vryearNow and KEYMONTH = vrmonthNow and KEYDAY = vrdayNow and BENHVIEN_ID = vrBenhVienId and rownum = 1; EXCEPTION WHEN NO_DATA_FOUND THEN Insert into LST_KEYDATA values ('SOTIEPNHAN', vryearNow, vrmonthNow, vrdayNow, '0', '0', '0', 0, vrBenhVienId); end; Update LST_KEYDATA set KEYVALUE = KEYVALUE + 1 where KEYTYPE = 'SOTIEPNHAN' and KEYYEAR = vryearNow and KEYMONTH = vrmonthNow and KEYDAY = vrdayNow and BENHVIEN_ID = vrBenhVienId returning KEYVALUE into vrkeyValue; COMMIT WORK; vrSoThuTu := LPAD(TO_CHAR(vrkeyValue), 7, '0'); vrSotiepnhan := 'TN' || SUBSTR(vryearNow, 3) || vrmonthNow || vrdayNow || '.' || vrSoThuTu;
insert into TT_TIEPNHAN ( TIEPNHAN_ID , SOTIEPNHAN , UUTIEN , TAIKHAM , BENHNHAN_ID , NOITIEPNHAN_ID , BENHVIEN_ID , TUYENKHAMBENH_ID , NGAYTIEPNHAN , THOIGIANTIEPNHAN , NAMTIEPNHAN , THANGTIEPNHAN , DOITUONG_ID , LYDOTIEPNHAN_ID , HINHTHUCDENKHAM_ID , TINHTRANGHEN , XACNHANCHIPHI_ID , THUTIENTRUOC , SOBHYT , BHYTTUNGAY , BHYTDENNGAY , BHYTNGAYCAP , BHYTTREN3NAM , BHYTTREN5NAM , NGAYMIENDONGCHITRA , LOAIBHYT , NGAYBHYT2 , SOCHUYENVIEN , HINHTHUCCHUYENVIEN_ID , NOIGIOITHIEU_ID , NGUOIGIOITHIEU_ID , THOIGIANCHUYENTUYEN , LYDOCHUYENVIEN_ID , ICDNOIGIOITHIEU_ID , CHANDOANNGT , TRAITUYEN , DIACHILIENHE , XAPHUONG_ID , QUANHUYEN_ID , TINHTHANH_ID , PROCESS_ID , TRANGTHAI , KHUVUCSONG_ID , THANHVIEN_ID , THE_ID , LOAITHANHVIEN_ID , SUDUNG_BHTN , VIP , GHICHU_TIEPNHAN , BAOLANH_VIENPHI , CONGTY_CHITRA_ID , NGUON_BENHNHAN_ID , LOAITIEPNHAN_ID , THONGTIN_KCB_BHYT , NGAYTAO , NGUOITAO_ID , LYDODENKHAM ) values ( #ID# , vrSotiepnhan , #UUTIEN# , #TAIKHAM# , #BENHNHAN_ID# , #NOITIEPNHAN_ID# , #BENHVIEN_ID# , #TUYENKHAMBENH_ID# , #NGAYTIEPNHAN# , #THOIGIANTIEPNHAN# , #NAMTIEPNHAN# , #THANGTIEPNHAN# , #DOITUONG_ID# , #LYDOTIEPNHAN_ID# , #HINHTHUCDENKHAM_ID# , #TINHTRANGHEN# , #XACNHANCHIPHI_ID# , #THUTIENTRUOC# , #SOBHYT# , #BHYTTUNGAY# , #BHYTDENNGAY# , #BHYTNGAYCAP# , #BHYTTREN3NAM# , #BHYTTREN5NAM# , #NGAYMIENDONGCHITRA# , #LOAIBHYT# , #NGAYBHYT2# , #SOCHUYENVIEN# , #HINHTHUCCHUYENVIEN_ID# , #NOIGIOITHIEU_ID# , #NGUOIGIOITHIEU_ID# , #THOIGIANCHUYENTUYEN# , #LYDOCHUYENVIEN_ID# , #ICDNOIGIOITHIEU_ID# , #CHANDOANNGT# , #TRAITUYEN# , #DIACHILIENHE# , #XAPHUONG_ID# , #QUANHUYEN_ID# , #TINHTHANH_ID# , #PROCESS_ID# , #TRANGTHAI# , #KHUVUCSONG_ID# , #THANHVIEN_ID# , #THE_ID# , #LOAITHANHVIEN_ID# , #SUDUNG_BHTN# , #VIP# , #GHICHU_TIEPNHAN# , #BAOLANH_VIENPHI# , #CONGTY_CHITRA_ID# , #NGUON_BENHNHAN_ID# , #LOAITIEPNHAN_ID# , #THONGTIN_KCB_BHYT# , #NGAYTAO# , #NGUOITAO_ID# , #LYDODENKHAM# ); END;

statements.statement:
select * from ( select ROW_NUMBER() OVER(ORDER BY tn.THOIGIANTIEPNHAN desc) AS ROWNUMBER, tn.*, bn.MAYTE, bn.TENBENHNHAN, NVL(TO_CHAR(NGAYSINH, 'DD/MM/YYYY'), TO_CHAR(NAMSINH)) as NGAYSINH, bn.DESCRIPTION GIOITINH, bn.DIACHILIENLAC from ( select T.*, CASE NVL(NN.LOAIBENHAN,NVL(NB.LOAIBENHAN,0)) WHEN 0 THEN '' WHEN 2 THEN 'DTNT' WHEN 1 THEN 'CC' ELSE 'NT' END LOAIBENHAN , DT.TENDOITUONG, NV.TENNHANVIEN from TT_TIEPNHAN T left join TT_NOITRU_NHAPVIEN NN ON NN.TIEPNHAN_ID = T.TIEPNHAN_ID left join TT_NOITRU_BENHAN NB ON NB.TIEPNHAN_ID = T.TIEPNHAN_ID left join TM_DOITUONG DT ON DT.DOITUONG_ID = T.DOITUONG_ID left join TM_NHANVIEN NV ON NV.NHANVIEN_ID = T.NGUOITAO_ID where T.BENHVIEN_ID = '$BENHVIEN_ID$'
trunc(T.NGAYTAO) BETWEEN trunc(#TUNGAY#) and trunc(#DENNGAY#)
) tn inner join (select A.*, B.DESCRIPTION from TT_BENHNHAN A left join TM_GENDER B on B.ID = A.GIOITINH) bn on tn.BENHNHAN_ID = bn.BENHNHAN_ID
(bn.MAYTE like '%$MAYTE$%' or bn.TENBENHNHAN like '%$MAYTE$%')
) tn where (tn.RowNumber between $FROMROW$ and $TOROW$) and tn.BENHVIEN_ID = '$BENHVIEN_ID$' order by tn.THOIGIANTIEPNHAN desc
