statements.statement:
WITH LIST_SOLO (SOLO, SOLO_BV, DONGIAMUA, VAT_MUA, DONGIAMUA_VAT, DONGIAVON, DONGIAVONVAT, VAT_BAN, TYLE_MARKUP, DONGIABAN , DONGIABAN_VP, DONGIA_BHYT, NGAYNHAP, HANDUNG, NHACUNGCAP_ID, TENNHACUNGCAP, ROWNUMBER, TOTAL_ROW, SOLUONG_TONKHO) AS ( SELECT DCS.SOLONHAP as SOLO, DCS.SOLOSANPHAM AS SOLO_BV, DSG.DONGIAMUA, DSG.TYLEVAT_DAUVAO AS VAT_MUA , DSG.DONGIAMUA * (1 + DSG.TYLEVAT_DAUVAO/100) AS DONGIAMUA_VAT, DSG.DONGIAVON, DSG.DONGIAVONVAT , D.TYLE_VAT AS VAT_BAN, DDD.TYLE AS TYLE_MARKUP, DSG.DONGIABAN, DSG.DONGIABAN * (1 + D.TYLE_VAT/100) AS DONGIABAN_VP , CASE WHEN DD.DONGIA IS NULL THEN NULL ELSE (CASE WHEN DD.DONGIA > NVL((DSG.DONGIAMUA * (1 + DSG.TYLEVAT_DAUVAO/100)),0) THEN (DSG.DONGIAMUA * (1 + DSG.TYLEVAT_DAUVAO/100)) ELSE DD.DONGIA END) END AS DONGIA_BHYT , DCS.NGAYNHAP, DCS.HANDUNG, DC.NHACUNGCAP_ID, NCC.TENNHACUNGCAP , ROW_NUMBER() OVER(ORDER BY DCS.NGAYNHAP DESC) as ROWNUMBER , COUNT(*) OVER() AS TOTAL_ROW, NVL(DT.SOLUONG,0) AS SOLUONG_TONKHO FROM TT_DUOC_CHUNGTU_SOLONHAP DCS INNER JOIN TT_DUOC_SOLONHAP_GIABAN DSG ON DCS.SOLONHAP_ID = DSG.SOLONHAP_ID AND DCS.DUOC_ID = DSG.DUOC_ID INNER JOIN TT_DUOC_CHUNGTU_CHITIET DCC ON DCC.SOLONHAP_ID = DCS.SOLONHAP_ID AND DCC.DUOC_ID = DCS.DUOC_ID INNER JOIN TT_DUOC_CHUNGTU DC ON DC.CHUNGTU_ID = DCC.CHUNGTU_ID AND DC.LOAICHUNGTU = 'N' AND DC.TRANGTHAI != 'HUY' INNER JOIN TM_NHACUNGCAP NCC ON NCC.NHACUNGCAP_ID = DC.NHACUNGCAP_ID INNER JOIN TM_DUOC D ON D.DUOC_ID = DCS.DUOC_ID INNER JOIN TM_LOAIDUOC LD ON LD.LOAIDUOC_ID = D.LOAIDUOC_ID LEFT JOIN TM_DUOC_DONGIA_DEFINITIONS DDD ON DDD.DUOC_DONGIA_DEFINITION_ID IN ( select TBL1.DUOC_DONGIA_DEFINITION_ID from ( SELECT * FROM TM_DUOC_DONGIA_DEFINITIONS ORDER BY DONGIA ASC ) TBL1 where TBL1.DONGIA >= DSG.DONGIAVON AND TBL1.LOAIVATTU_ID = LD.LOAIVATTU_ID AND ROWNUM = 1 ) LEFT JOIN TM_DUOC_DONGIA DD ON DD.DUOC_ID = DCS.DUOC_ID LEFT JOIN TM_LOAIGIA LG ON LG.LOAIGIA_ID = DD.LOAIGIA_ID AND LG.TAMNGUNG = '0' LEFT JOIN LST_DICTIONARY DIC ON DIC.DICTIONARY_ID = LG.NHOMGIA_ID AND DIC.DICTIONARY_TYPE_CODE = 'NhomGia' AND DIC.DICTIONARY_CODE = 'BHYT' LEFT JOIN TT_DUOC_TONKHO DT ON DT.DUOCTONKHO_ID IN ( select TBL2.DUOCTONKHO_ID from ( SELECT * FROM TT_DUOC_TONKHO ORDER BY SOLUONG DESC ) TBL2 where TBL2.DUOC_ID = DCS.DUOC_ID AND TBL2.SOLONHAP_ID = DCS.SOLONHAP_ID AND ROWNUM = 1 ) WHERE DCS.BENHVIEN_ID = '$BENHVIEN_ID$' AND DCS.DUOC_ID = '$DUOC_ID$' AND (DD.DUOC_ID IS NULL OR DIC.DICTIONARY_CODE IS NOT NULL) AND ('$FILTER_ID$' = '0' OR ('$FILTER_ID$' = '1' AND NVL(DT.SOLUONG,0) <= 0) OR (('$FILTER_ID$' = '2' AND NVL(DT.SOLUONG,0) > 0) OR ('$FILTER_ID$' = '3' and trunc(SYSDATE) > trunc(DCS.HANDUNG) )))
DCS.SOLOSANPHAM = '$SOLO_BV$'
) SELECT * FROM LIST_SOLO WHERE ROWNUMBER BETWEEN '$FROM_ROW$' AND '$TO_ROW$' ORDER BY NGAYNHAP DESC
