statements.statement:
select CTCT.CHUNGTUCHITIET_ID,CTCT.CHUNGTU_ID,CTCT.HANGHOA_ID,CTCT.MAHANGHOA,CTCT.TENHANGHOA,CTCT.DONVITINH_ID,CTCT.TENDONVITINH,CTCT.LOAI_ID,L.TEN as TENLOAI,SOLUONGYEUCAU,CTCT.MOTA, CTCT.THUONGHIEU,CTCT.KICHTHUOC,CTCT.VATLIEU,CTCT.GHICHU,CTCT.FILE_NAME,CTCT.FULLPATHSERVER,CTCT.FULLPATHLOCAL,CTCT.SIZE,CTCT.FILE_NAME_PO,CTCT.FULLPATHSERVER_PO,CTCT.FULLPATHLOCAL_PO,CTCT.SIZE_PO,CTCT.STATE_CODE AS CT_STATE_CODE,CTCT.STATE_NAME AS CT_STATE_NAME,CTCT.NGUOIDUYET_ID AS CT_NGUOIDUYET_ID,CTCT.TENNGUOIDUYET AS CT_TENNGUOIDUYET,CTCT.NGAYGIODUYET AS CT_NGAYGIODUYET, CTCT.MACHONGIA,CTCT.TENCHONGIA,CTCT.DONGIA,CTCT.TYLEVAT,CTCT.GIATRIVAT,CTCT.DONGIASAUVAT,CTCT.THANHTIEN,CTCT.NHACUNGCAP_ID AS CT_NHACUNGCAP_ID,CTCT.TENNHACUNGCAP AS CT_TENNHACUNGCAP,CTCT.DONGIAHOPDONG,CTCT.TUNGAYHOPDONG, CTCT.DENNGAYHOPDONG,CTCT.MATONGHOP, CTCT.CHUNGTUTONGHOP_ID,CTCT.MALOAIPHIEU,CTCT.BAOHANHTUNGAY,CTCT.BAOHANHDENNGAY,CTCT.TENNGUOITAO,CTCT.TENNGUOICAPNHAT,CTCT.NGAYTAO,CTCT.NGUOITAO_ID,CTCT.NGAYCAPNHAT,CTCT.NGUOICAPNHAT_ID, PR.MACHUNGTUPR,(CASE WHEN PR.MATRANGTHAIPR != '1' AND PR.MATRANGTHAIPR != '2' THEN ISNULL(CT.TENNGUOIDUYETCAP3,ISNULL(CT.TENNGUOIDUYETCAP2,CT.TENNGUOIDUYETCAP1))+' '+PR.TENTRANGTHAIPR ELSE PR.TENTRANGTHAIPR END) AS TENTRANGTHAIPR,PO.MACHUNGTUPO, (CASE WHEN PO.MATRANGTHAIPO != '1' AND PO.MATRANGTHAIPO != '2' THEN ISNULL(CT.TENNGUOIDUYETCAP3,ISNULL(CT.TENNGUOIDUYETCAP2,CT.TENNGUOIDUYETCAP1))+' '+PO.TENTRANGTHAIPO ELSE PO.TENTRANGTHAIPO END) AS TENTRANGTHAIPO FROM TT_PUR_CHUNGTU CT INNER JOIN TT_PUR_CHUNGTUCHITIET CTCT ON CT.CHUNGTU_ID = CTCT.CHUNGTU_ID INNER JOIN TM_PUR_LOAI AS L ON L.ID = CTCT.LOAI_ID LEFT JOIN ( SELECT XX.MACHUNGTU AS MACHUNGTUPR,XX.STATE_CODE AS MATRANGTHAIPR,YY.MATONGHOP AS MACHUNGTU,YY.CHUNGTUTONGHOP_ID AS CHUNGTUCHITIET_ID,YY.CHUNGTUCHITIET_ID AS CHUNGTUCHITIET_IDPR, (CASE WHEN XX.STATE_CODE='1' THEN N'Tạo mới' ELSE CASE WHEN XX.STATE_CODE='2' THEN N'Gởi duyệt' ELSE CASE WHEN XX.STATE_CODE='4' THEN N'đã từ chối' ELSE N'đã duyệt' END END END) AS TENTRANGTHAIPR FROM TT_PUR_CHUNGTU XX INNER JOIN TT_PUR_CHUNGTUCHITIET YY ON XX.CHUNGTU_ID=YY.CHUNGTU_ID WHERE XX.PROCESS_CODE='PR' AND XX.BENHVIEN_ID = '$BENHVIEN_ID$'
YY.MATONGHOP = '$MACHUNGTU$'
) PR ON CT.MACHUNGTU=PR.MACHUNGTU AND CTCT.CHUNGTUCHITIET_ID=PR.CHUNGTUCHITIET_ID LEFT JOIN ( SELECT XX.MACHUNGTU AS MACHUNGTUPO,XX.STATE_CODE AS MATRANGTHAIPO,YY.MATONGHOP AS MACHUNGTUPR, YY.CHUNGTUTONGHOP_ID AS CHUNGTUCHITIET_IDPR, (CASE WHEN XX.STATE_CODE='1' THEN N'Tạo mới' ELSE CASE WHEN XX.STATE_CODE='2' THEN N'Gởi duyệt' ELSE CASE WHEN XX.STATE_CODE='4' THEN N'đã từ chối' ELSE N'đã duyệt' END END END) AS TENTRANGTHAIPO FROM TT_PUR_CHUNGTU XX INNER JOIN TT_PUR_CHUNGTUCHITIET YY ON XX.CHUNGTU_ID=YY.CHUNGTU_ID INNER JOIN TT_PUR_CHUNGTU ZZ ON YY.MATONGHOP=ZZ.MACHUNGTU INNER JOIN TT_PUR_CHUNGTUCHITIET ZZ1 ON ZZ.CHUNGTU_ID=ZZ1.CHUNGTU_ID AND ZZ1.CHUNGTUCHITIET_ID=YY.CHUNGTUTONGHOP_ID WHERE XX.PROCESS_CODE='PO' AND XX.BENHVIEN_ID = '$BENHVIEN_ID$' AND XX.CHUNGTU_IDPOMOI IS NULL
ZZ1.MATONGHOP = '$MACHUNGTU$'
) PO ON PR.MACHUNGTUPR=PO.MACHUNGTUPR AND PR.CHUNGTUCHITIET_IDPR=PO.CHUNGTUCHITIET_IDPR where CTCT.BENHVIEN_ID = '$BENHVIEN_ID$' AND CTCT.CHUNGTU_ID = '$CHUNGTU_ID$' ORDER BY TENHANGHOA
